     */
    public function __construct($con, $userId, $emailOrigem, $emailDestino, $assunto, $msg, $anexo, $motivo, $lead) {
        $image = base64_decode("");
        
        $result = mysqli_query($con, sprintf("SELECT * FROM cad_utilizadores WHERE id=%s", $userId));
        if($result){
