            $msg = $msg.$footer;
             //Enviar o email

            $mail = new PHPMailer();

            $mail->DKIM_domain ="gestlifes.com";
            $mail->DKIM_private ='8d57af3432d77e6._domainkey.gestlifes.com';
            $mail->DKIM_selector='14400.gestlifes.com';
            $mail->DKIM_passphrase='';
            $mail->DKIM_identity=$emailOrigem;


            //$mail->SMTPDebug=4;
            $mail->isSMTP();
